using Components;using System;using MessagePack;using HECSFramework.Serialize;using Commands;namespace HECSFramework.Core{	[MessagePackObject, Serializable]	public struct PredicatesComponentResolver : IResolver<PredicatesComponent>, IResolver<PredicatesComponentResolver,PredicatesComponent>, IData	{		[Key(0)]		public byte[] savePredicates;		public PredicatesComponentResolver In(ref PredicatesComponent predicatescomponent)		{			predicatescomponent.BeforeSync();			this.savePredicates = predicatescomponent.savePredicates;			return this;		}		public void Out(ref IEntity entity)		{			var local = entity.GetPredicatesComponent();			Out(ref local);		}		public void Out(ref PredicatesComponent predicatescomponent)		{			predicatescomponent.savePredicates = this.savePredicates;			predicatescomponent.AfterSync();		}	}}