using System;using HECSFramework.Serialize;namespace Commands{	[Serializable]	public struct SetPlayerControllerAnimatorState : ISetAnimatorState	{		public FloatId HorizontalSpeed;		public FloatId VerticalSpeed;		public BoolId isJumping;		public BoolId isAttacking;		public BoolId isDamaging;		public BoolId isDead;		public void SetState(AnimatorState animatorState)		{			animatorState.SetFloat(HorizontalSpeed.Id, HorizontalSpeed.Value);			animatorState.SetFloat(VerticalSpeed.Id, VerticalSpeed.Value);			animatorState.SetBool(isJumping.Id, isJumping.Value);			animatorState.SetBool(isAttacking.Id, isAttacking.Value);			animatorState.SetBool(isDamaging.Id, isDamaging.Value);			animatorState.SetBool(isDead.Id, isDead.Value);		}	}	[Serializable]	public struct SetKeyControllerAnimatorState : ISetAnimatorState	{		public BoolId isPickUped;		public void SetState(AnimatorState animatorState)		{			animatorState.SetBool(isPickUped.Id, isPickUped.Value);		}	}	[Serializable]	public struct SetChomperControllerAnimatorState : ISetAnimatorState	{		public BoolId isDamaging;		public BoolId isDead;		public BoolId isAttacking;		public void SetState(AnimatorState animatorState)		{			animatorState.SetBool(isDamaging.Id, isDamaging.Value);			animatorState.SetBool(isDead.Id, isDead.Value);			animatorState.SetBool(isAttacking.Id, isAttacking.Value);		}	}	[Serializable]	public struct SetSpitterControllerAnimatorState : ISetAnimatorState	{		public BoolId isAttacking;		public BoolId isDamaging;		public BoolId isDead;		public void SetState(AnimatorState animatorState)		{			animatorState.SetBool(isAttacking.Id, isAttacking.Value);			animatorState.SetBool(isDamaging.Id, isDamaging.Value);			animatorState.SetBool(isDead.Id, isDead.Value);		}	}}